import React, { useState } from "react";
import { CampoTexto } from "./CampoTexto";

function Imc(altura: number, peso: number) {
  return (
    altura / (peso ** 2)
  );
}

interface IMCFormModel {
  altura: string
  peso: string
}

interface FormElements extends HTMLFormControlsCollection {
  pesoInput: HTMLInputElement
  alturaInput: HTMLInputElement
}

export function Formulario() {

  function calcularImc(event: React.FormEvent<HTMLFormElement>) {
    var n_altura = Number(document.getElementById("altura")) / 100;
    var n_peso = Number(document.getElementById("peso"));
    // const target = event.target
    // const altura = target.altura
    debugger

    var x = Imc(n_altura, n_peso);
    console.log(x)
  }

  const [imcFormValues, setImcFormValues] = useState<IMCFormModel>({altura: "0", peso: "0"})

  const handleChangeAltura = () => {
    setImcFormValues({})
  }

  const handleChangePeso = (params) => {
    setImcFormValues({})
  }

  const altura = "altura";
  const peso = "peso";
  return (
    <form onSubmit={calcularImc}>
      <CampoTexto for={altura} id={altura} name={altura} text="Altura (cm):" onChange={handleChangeAltura} />
      <CampoTexto for={peso} id={peso} name={peso} text="Peso (kg):" />
      <button type="submit">Calcular</button>
    </form>
  );
}
